package Week03;

public class A01_For {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 1; i <= 9; i++) {
			System.out.println("2 * " + i + " = " + 2 * i);
		}
		for (int i = 9; i > 0; i--) {
			System.out.println("2 * " + i + " = " + 2 * i);
		}
	}
}
